script:
cur_frm.add_fetch('person_in_charge', 'employee_name', 'person_in_charge_name');
cur_frm.set_query("person_in_charge", function() {
    return {
        "filters": {
            "department": ["like", "Maintenance - "]
        }
    }
});
cur_frm.add_fetch('sub_contractor_contact', 'supplier_name', 'sub_contractor_name');
cur_frm.set_query("sub_contractor_contact", function() {
    return {
        filters: {
            'supplier_group': 'Sub-Contractor'
        }
    }
});
cur_frm.fields_dict['materials_required'].grid.get_field('material_request').get_query = function(doc, cdt, cdn) {
    var child = locals[cdt][cdn]
    return {
        filters: [
            ['Material Request', 'docstatus', '=', '0'],
            ['Material Request', 'docstatus', '=', '1']
        ]
    }
}
frappe.ui.form.on('Issue Materials Detail', {
    material_request: function(frm, cdt, cdn) {
        var material = locals[cdt][cdn];
        if (material.material_request) {
            frappe.call({
                "method": "frappe.client.get_value",
                "args": {
                    doctype: "Material Request",
                    filters: {
                        "name": material.material_request
                    },
                    fieldname: "status"
                },
                callback: function(r) {
                    frappe.model.set_value(cdt, cdn, "material_status", r.message.status);
                }
            });
        }
    }
})
frappe.ui.form.on('Issue', {
    property_name: function(frm, cdt, cdn) {
        frappe.model.set_value(cdt, cdn, "customer", "");
	if (frm.doc.property_name) {
            frappe.call({
                method: "frappe.client.get_value",
                args: {
                    doctype: "Property",
                    fieldname: "status",
                    filters: {
                        name: frm.doc.property_name
                    },
                },
                callback: function(r, rt) {
                    if (r.message) {
                        if (r.message.status == "On lease") {
                            frappe.call({
                                method: "frappe.client.get_value",
                                args: {
                                    doctype: "Lease",
                                    fieldname: "customer",
                                    filters: {
                                        property: frm.doc.property_name
                                    },
                                },
                                callback: function(r, rt) {
                                    if (r.message) {
                                        frappe.model.set_value(cdt, cdn, "customer", r.message.customer);
                                    }
                                }
                            });
                        }
                    }
                }
            });
        } else {
            frappe.model.set_value(cdt, cdn, "customer", "");
        }
    }
})

frappe.ui.form.on('Daily Checklist', 'area', function(frm, cdt, cdn) {
    var doc = locals[cdt][cdn];
    if (doc.area) {
        frappe.call({
            method: "frappe.client.get",
            args: {
                name: doc.area,
                doctype: "Checklist Checkup Area"
            },
            callback(r) {
                console.log(r);
                if (r.message) {
                    for (var row in r.message.task) {
                        var child = frm.add_child("daily_checklist_detail");
                        frappe.model.set_value(child.doctype, child.name, "checklist_task", r.message.task[row].task_name);
                        refresh_field("daily_checklist_detail");
                    }
                }
            }
        })
    }
})
cur_frm.fields_dict['daily_checklist_detail'].grid.get_field('job_card').get_query = function(doc, cdt, cdn) {
    var child = locals[cdt][cdn]
    return {
        filters: [
            ['Issue', 'docstatus', '=', '0'],
            ['Issue', 'docstatus', '=', '1']
        ]
    }
};

frappe.ui.form.on("Key Set Detail", "returned", function(frm) {
    if (cur_frm.doc.returned) cur_frm.set_value("return_date_and_time", frappe.datetime.now_datetime());
    else {
        cur_frm.set_value("return_date_and_time", "");
    }
});

cur_frm.add_fetch('guard_empid', 'employee_name', 'guard_name');
