css:
@media screen {
  .print-format {
     padding-left: 10mm;
     padding-right: 10mm;
     padding-top: 5mm;
     padding-bottom: 2mm;
      width: 5.8in;
      min-height: 8.3in;
      font-size: 7pt;
  }
  .print-format td, .print-format th {
    vertical-align: top !important;
   padding: 1px !important;
  }

  .print-format h1 { font-size: 19pt;}
  .print-format h2 { font-size: 17pt;}
  .print-format h3 { font-size: 15pt;}
  .print-format h4 { font-size: 13pt;}
  .print-format h5 { font-size: 9pt;}
  .print-format h6 { font-size: 8px;}
}
.hr_class {
   margin-top: 6px;
   margin-bottom: 6px;
}

.clearfix:after {
    /* clear: both; */
}

.col-xs-7 {
    position: relative;
    min-height: 1px;
    padding-right: 5px;
    padding-left: 5px;
}
